import React, { useEffect, useState } from "react";

import "./style.css";

import { Link } from "react-router-dom";

//import { useLog } from "./../../contexts/LoggedContext";

export default function Logon() {
  const [logados, setLogados] = useState([
    {
      login: "Daniel",
      password: "Senha",
    },
  ]);

  const init = () => {
    let data = localStorage.getItem("lista-de-cadastros");
    try {
      setLogados(JSON.parse(data));
    } catch (err) {
      console.log("setLogados não funcionou!" + err);
    }
    localStorage.setItem("lista-de-cadastros", JSON.stringify(logados));
  };

  useEffect(init, []);

  return (
    <>
      <div className="leftball" />
      <Link to="/">
        <button
          style={{
            width: "100px",
            marginLeft: "20px",
            fontSize: "20px",
            color: "black",
            fontFamily: "PMingLiU-ExtB",
            position: "absolute",
          }}
        >
          Voltar Logados.login : {logados} daaaaaaaaaaa
        </button>
      </Link>
      <div className="registerContainer">
        <div className="formRegisterContainer">
          <div className="placeholderContainer">
            <div className="formPlaceholderContainer">
              <input
                onChange={(e) => setLogados(e.target.value)}
                className="placeholder"
                placeholder="Login"
              ></input>
              <input className="placeholder" placeholder="Senha"></input>
              <Link to="/hall">
                <button className="submit" type="submit">
                  Próximo
                </button>
              </Link>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
