import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

import "./styles.css";

export default function Register() {
  function handleSubmit(e) {
    e.preventDefault();
    console.log("FIRSTSTEP");
    setData(nome, sexo, dataNascimento, endereco, cpf, whatsapp);
  }
  const [data, setData] = useState([
    {
      nome: "nome",
      sexo: "sexo",
      dataNascimento: "dataNascimento",
      endereco: "endereco",
      cpf: "cpf",
      whatsapp: "whatsapp",
    },
  ]);

  useEffect(() => {
    localStorage.setItem("stepone", data);
    //localStorage.clear();
  }, [data]);

  const [nome, setNome] = useState("");
  const [sexo, setSexo] = useState("");
  const [dataNascimento, setDataNascimento] = useState("");
  const [endereco, setEndereco] = useState("");
  const [cpf, setCpf] = useState("");
  const [whatsapp, setWhatsapp] = useState("");
  return (
    <>
      <div className="leftball" />
      <Link to="/">
        <button
          style={{
            width: "100px",
            marginLeft: "20px",
            fontSize: "20px",
            color: "black",
            fontFamily: "PMingLiU-ExtB",
            position: "absolute",
          }}
        >
          Voltar
        </button>
      </Link>
      <div className="registerContainer">
        <div className="formRegisterContainer">
          <div className="placeholderContainer">
            <form className="formPlaceholderContainer">
              <input
                className="placeholder"
                placeholder="Nome"
                onBlur={(e) => setNome(e.target.value)}
              ></input>
              <input
                className="placeholder"
                placeholder="Sexo"
                onBlur={(e) => setSexo(e.target.value)}
              ></input>
              <input
                className="placeholder"
                placeholder="Data de Nascimento"
                onBlur={(e) => setDataNascimento(e.target.value)}
              ></input>
              <input
                className="placeholder"
                placeholder="Endereço"
                onBlur={(e) => setEndereco(e.target.value)}
              ></input>
              <input
                className="placeholder"
                placeholder="CPF"
                onBlur={(e) => setCpf(e.target.value)}
              ></input>
              <input
                className="placeholder"
                placeholder="Número de Whatsapp"
                onChange={(e) => setWhatsapp(e.target.value)}
              ></input>
              <Link to="/RegisterSecondStep">
                <button
                  onClick={() => handleSubmit}
                  className="submit"
                  type="submit"
                >
                  Submit
                </button>
              </Link>
            </form>
          </div>
        </div>
      </div>
    </>
  );
}
