import React, { useState, useEffect } from "react";

import "./style.css";

import { Link } from "react-router-dom";

export default function RegisterSecondStep() {
  function handleSubmit(e) {
    console.log("SECONDSTEP");
    setData({
      matricula,
      batalhaoAtual,
      batalhaoInteresse,
      comportamento,
    });
  }

  const [matricula, setMatricula] = useState("");
  const [batalhaoAtual, setBatalhaoAtual] = useState("");
  const [batalhaoInteresse, setBatalhaoInteresse] = useState("");
  const [comportamento, setComportamento] = useState("");

  const [data, setData] = useState({
    matricula: "matricula",
    batalhaoAtual: "batalhaoAtual",
    batalhaoInteresse: "batalhaoInteresse",
    comportamento: "comportamento",
  });

  useEffect(() => {
    localStorage.setItem("steptwo", data);
    //localStorage.clear();
  }, [data]);

  return (
    <>
      <div className="leftball" />
      <Link to="/register">
        <button
          style={{
            width: "100px",
            marginLeft: "20px",
            fontSize: "20px",
            color: "black",
            fontFamily: "PMingLiU-ExtB",
            position: "absolute",
          }}
        >
          Voltar
        </button>
      </Link>
      <div className="registerContainer">
        <div className="formRegisterContainer">
          <div className="placeholderContainer">
            <form className="formPlaceholderContainer">
              <input
                className="placeholder"
                placeholder="Número de Matrícula"
                onChange={(e) => setMatricula(e.target.value)}
              ></input>
              <input
                className="placeholder"
                placeholder="Batalhão Atual"
                onChange={(e) => setBatalhaoAtual(e.target.value)}
              ></input>
              <input
                className="placeholder"
                placeholder="Batalhão de Interesse"
                onChange={(e) => setBatalhaoInteresse(e.target.value)}
              ></input>
              <input className="placeholder" placeholder="Órgão"></input>
              <input
                className="placeholder"
                placeholder="Comportamento"
                onChange={(e) => setComportamento(e.target.value)}
              ></input>
              <Link to="/registerthirdstep" onClick={() => handleSubmit}>
                <button className="submit" type="submit">
                  Próximo
                </button>
              </Link>
            </form>
          </div>
        </div>
      </div>
    </>
  );
}
