import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

import "./style.css";

export default function RegisterThirdStep() {
  const [login, setLogin] = useState("");
  const [senha, setSenha] = useState("");

  const [data, setData] = useState({
    login: "login",
    senha: "senha",
  });

  var a;
  var b;
  var c;

  useEffect(() => {
    localStorage.setItem("stepthree", data);
  }, [data]);

  function handleSubmit(e) {
    setData(login, senha);
    a = localStorage.getItem("stepone");
    b = localStorage.getItem("steptwo");
    c = localStorage.getItem("stepthree");
    console.log("a = " + a);
    console.log("b = " + b);
    console.log("c = " + c);
  }

  return (
    <>
      <div className="leftball" />
      <Link to="/registersecondstep">
        <button
          style={{
            width: "100px",
            marginLeft: "20px",
            fontSize: "20px",
            color: "black",
            fontFamily: "PMingLiU-ExtB",
            position: "absolute",
          }}
        >
          Voltar
        </button>
      </Link>
      <div className="registerContainer">
        <div className="formRegisterContainer">
          <div className="placeholderContainer">
            <form
              className="formPlaceholderContainer"
              onSubmit={(e) => handleSubmit(e)}
            >
              <input
                className="placeholder"
                placeholder="Login"
                onChange={(e) => setLogin(e.target.value)}
              ></input>
              <input
                className="placeholder"
                placeholder="Senha"
                onChange={(e) => setSenha(e.target.value)}
              ></input>
              <Link to="/hall">
                <button className="submit" type="submit">
                  Registrar
                </button>
              </Link>
            </form>
          </div>
        </div>
      </div>
    </>
  );
}
